    "contracts/v1Interfaces/IEqueusUserRegistry.sol": {
      "content": "// contracts/IEqueusUserRegistry.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

struct User {
    string username;
    string sponsor;
    address addr;
    uint256 activeLines;
    uint256 activeLinesExpireTime;
    uint256 createdAt;
    bool kycVerified;
    bool hasSocialMarketingSystemEnabled;
    bool isSponsorshipEnabled;
}

/**
 * @title Equeus User Registry Interface.
 * @notice Interface for the Equeus User Registry.
 */
interface IEqueusUserRegistry {
    event UserRegistered(string username, string sponsor, uint256 timestamp);
    event UserImported(
        string username,
        string sponsor,
        address addr,
        uint256 activeLines,
        uint256 activeLinesExpireTime,
        uint256 timestamp
    );
    event UserAddressUpdated(string username, address addr, uint256 timestamp);
    event KYCVerified(string username, bool isVerified, uint256 timestamp);

    /**
     * @dev Checks if a user exists.
     * @param _username The username of the user.
     */
    function usernameExists(string memory _username) external returns (bool);

    /**
     * @dev Registers a new user. Callable only by auth operators.
     * @param _username The username of the user.
     * @param _sponsor The sponsor of the user. The sponsor must be a registered user.
     */
    function registerUser(
        string memory _username,
        string memory _sponsor
    ) external;

    /**
     * @dev Gets the user by address.
     * @param _addr The address of the user.
     */
    function getUserByAddress(
        address _addr
    ) external view returns (User memory);

    /**
     * @dev Gets the user by username.
     * @param _username The username of the user.
     * @return The username, address, active lines, active lines expire time, created at and sponsorship enabled status of the user.
     */
    function getUserByUsername(
        string memory _username
    ) external view returns (User memory);

    /**
     * @dev Gets the address of the user by username.
     * @param _username The username of the user.
     * @return The address of the user.
     */
    function getAddressByUsername(
        string memory _username
    ) external view returns (address);

    /**
     * @dev Sets the address of the user by username.
     * @param _username The username of the user.
     * @param _addr The address of the user.
     */
    function setUserAddress(string memory _username, address _addr) external;

    /**
     * @dev checks if a username is valid.
     * @param _username The username of the user.
     */
    function isValidUsername(string memory _username) external returns (bool);

    /**
     * @dev Checks if the user has the social marketing system enabled.
     * @param _username The username of the user.
     */
    function isSocialMarketingSystemEnabled(
        string memory _username
    ) external returns (bool);

    /**
     * @dev Gets the data needed to process the commission.
     * @param _username the username of the user
     * @return sponsor
     * @return address
     * @return active lines
     * @return active lines expire time
     */
    function getCommissioninfo(
        string memory _username
    ) external returns (string memory, address, uint256, uint256, uint256);

    /**
     * @dev Sets the active lines of the user.
     */
    function setUserActiveLines(
        string memory _username,
        uint256 _activeLines,
        uint256 _activeLinesExpireTime
    ) external;

    /**
     * @dev Sets the KYC verification status of the user.
     */
    function setKYCVerified(string memory _username, bool _isVerified) external;

    /**
     * @dev Sets the sponsorship enabled status of the user.
     */
    function setSponsorshipEnabled(
        string memory _username,
        bool _isEnabled
    ) external;

    /**
     * @dev Sets the social marketing system enabled status of the user.
     */
    function setSocialMarketingSystemEnabled(
        string memory _username,
        bool _isEnabled
    ) external;
}
"
