contract RareDoughShop is Functional, Ownable, IERC2981, IERC165, IERC1155, IERC1155MetadataURI {
    using Address for address;

    // Mapping from token ID to account balances
    mapping(uint256 => mapping(address => uint256)) private _balances;
    // Mapping from account to operator approvals
    mapping(address => mapping(address => bool)) private _operatorApprovals;

    string private _uri; //This has to stay here because functions here depend on it

	mapping(uint256 => address) coinContract;
	mapping(uint256 => uint256) price;
	mapping(uint256 => bool) saleActive;
	mapping(uint256 => uint256) totalSupply;
	mapping(uint256 => uint256) numMinted;
    mapping(uint256 => uint256) maxPerTxn;

    address public treasuryWallet;

    address public breadToken;
    address public burnOven;

    constructor() {
        _setURI("https://raredough.com/assets/");
        treasuryWallet = address(0x35CB8091bd7De74B04cDBfF8CC508413Ce692F8d);
        breadToken = address(0xb8e57A05579b1F4c42DEc9e18E0b665B0dB5277f);
        burnOven = address(0x5Ebd7084286503c932F0c3435503834424b191Fc);
    }

    function royaltyInfo( uint256 tokenId_, uint256 salePrice_ ) external view returns ( address receiver, uint256 royaltyAmount ){
        return (treasuryWallet, (salePrice_ / 20));
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return
            interfaceId == type(IERC1155).interfaceId ||
            interfaceId == type(IERC1155MetadataURI).interfaceId ||
            RareDoughShop.supportsInterface(interfaceId);
    }

    function airdrop( address dropWallet, uint256 tokenId, uint256 qty ) external onlyOwner {
        require( totalSupply[tokenId] >= qty + numMinted[tokenId], "Not Enough Supply");
        numMinted[tokenId] += qty;
        _mint( dropWallet, tokenId, qty, "");
    }

	function createPizza( uint tokenId, address setCoinContract, uint256 setPrice, uint256 setTotalSupply, uint256 setMaxPerTxn) external onlyOwner {
		price[tokenId] = setPrice;
		coinContract[tokenId] = setCoinContract;
		totalSupply[tokenId] = setTotalSupply;
        maxPerTxn[tokenId] = setMaxPerTxn;
	}
	
	function buyPizzas( uint256 tokenId, uint256 qty ) external reentryLock {
		require( saleActive[tokenId], "That NFT is not for sale" );
		require( totalSupply[tokenId] >= qty + numMinted[tokenId], "Not Enough Supply");
        require( qty <= maxPerTxn[tokenId], "limit per txn exceeded");
		
		ERC20 Coin = ERC20( coinContract[tokenId] );
		uint256 salePrice = qty * price[tokenId];
		require( Coin.balanceOf(_msgSender()) >= salePrice, "Insufficient Funds" );
		
        if (coinContract[tokenId] == breadToken){
            // use the bread oven proxy code
            Coin.proxyTransfer( _msgSender(), treasuryWallet, salePrice );
        } else {
		    // requires token transfer to be approved ahead of time
	    	Coin.transferFrom( _msgSender(), treasuryWallet, salePrice );
        }
		
        numMinted[tokenId] += qty;
		_mint( _msgSender(), tokenId, qty, "");
	}

    function setTreasury( address newWallet ) external onlyOwner {
        treasuryWallet = newWallet;
    }
	
	function setPrice( uint256 tokenId, address setCoinContract, uint256 setPrice ) external onlyOwner {
		price[tokenId] = setPrice;
		coinContract[tokenId] = setCoinContract;
	}

    function setMaxPerTxn( uint256 tokenId, uint256 newMaxLimitPerTxn) external onlyOwner {
        maxPerTxn[tokenId] = newMaxLimitPerTxn;
    }

    function getPrice( uint256 tokenId ) public view returns(uint256) {
        return price[tokenId];
    }
	
    function getPurchaseCoin( uint256 tokenId ) public view returns(address) {
        return coinContract[tokenId];
    }

    function getMaxSupply( uint256 tokenId ) public view returns(uint256) {
        return totalSupply[tokenId];
    }

	function setTotalSupply( uint256 tokenId, uint256 setTotalSupply ) external onlyOwner {
		totalSupply[tokenId] = setTotalSupply;
	}
	
    function activateSale( uint256 tokenId ) external onlyOwner {
        saleActive[tokenId] = true;
    }
	
    function deactivateSale( uint256 tokenId ) external onlyOwner {
        saleActive[tokenId] = false;
    }

    function setURI( string memory newURI ) external onlyOwner {
        _setURI( newURI );
    }  

    function setBreadToken( address newContract ) external onlyOwner{
        breadToken = newContract;
    }

    function setBurnOven( address newContract ) external onlyOwner{
        burnOven = newContract;
    }

    // added burn functions, error checking included.
    function burn(address _from, uint256 _id, uint256 _quantity) public {
        require( _from == _msgSender() 
                || isApprovedForAll(_from, _msgSender())
                || _msgSender() == burnOven
                ,"ERC1155: transfer caller is not owner nor approved");
        _burn(_from, _id, _quantity);
    }

	function batchBurn(address _from, uint256[] memory _ids, uint256[] memory _quantities) public {
        require( _from == _msgSender() 
                || isApprovedForAll(_from, _msgSender())
                || _msgSender() == burnOven
                ,"ERC1155: transfer caller is not owner nor approved");
        _burnBatch(_from, _ids, _quantities);
    }

    /**
     * @dev See {IERC1155MetadataURI-uri}.
     *
     * This implementation returns the same URI for *all* token types. It relies
     * on the token type ID substitution mechanism
     * https://eips.ethereum.org/EIPS/eip-1155#metadata[defined in the EIP].
     *
     * Clients calling this function must replace the `\{id\}` substring with the
     * actual token type ID.
     */
    function uri(uint256 seriesId) public view virtual override returns (string memory) {
        return string(abi.encodePacked( _uri, toString(seriesId), ".json" ));
    }

    /**
     * @dev See {IERC1155-balanceOf}.
     *
     * Requirements:
     *
     * - `account` cannot be the zero address.
     */
    function balanceOf(address account, uint256 id) public view virtual override returns (uint256) {
        require(account != address(0), "ERC1155: balance query for the zero address");
        return _balances[id][account];
    }

    /**
     * @dev See {IERC1155-balanceOfBatch}.
     *
     * Requirements:
     *
     * - `accounts` and `ids` must have the same length.
     */
    function balanceOfBatch(address[] memory accounts, uint256[] memory ids)
        public
        view
        virtual
        override
        returns (uint256[] memory)
    {
        require(accounts.length == ids.length, "ERC1155: accounts and ids length mismatch");

        uint256[] memory batchBalances = new uint256[](accounts.length);

        for (uint256 i = 0; i < accounts.length; ++i) {
            batchBalances[i] = balanceOf(accounts[i], ids[i]);
        }

        return batchBalances;
    }

    /**
     * @dev See {IERC1155-setApprovalForAll}.
     */
    function setApprovalForAll(address operator, bool approved) public virtual override {
        require(_msgSender() != operator, "ERC1155: setting approval status for self");

        _operatorApprovals[_msgSender()][operator] = approved;
        emit ApprovalForAll(_msgSender(), operator, approved);
    }

    /**
     * @dev See {IERC1155-isApprovedForAll}.
     */
    function isApprovedForAll(address account, address operator) public view virtual override returns (bool) {
        return _operatorApprovals[account][operator];
    }

    /**
     * @dev See {IERC1155-safeTransferFrom}.
     */
    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public virtual override {
        require(
            from == _msgSender() || isApprovedForAll(from, _msgSender()),
            "ERC1155: caller is not owner nor approved"
        );
        _safeTransferFrom(from, to, id, amount, data);
    }

    /**
     * @dev See {IERC1155-safeBatchTransferFrom}.
     */
    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
