    "contracts/AuctionHouse.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";

import "./ISettingsStore.sol";
import "./IAcceptTreasure.sol";

contract AuctionHouse is IERC721Receiver {
  using SafeERC20 for IERC20;

  uint8 public constant LISTING_TYPE_ERC721 = 1;
  uint8 public constant LISTING_TYPE_ERC20 = 2;

  uint8 public constant LISTING_STATE_NONE = 0;
  uint8 public constant LISTING_STATE_CREATED = 1;
  uint8 public constant LISTING_STATE_OPEN = 2;
  uint8 public constant LISTING_STATE_CLOSED = 3;

  struct Listing {
    uint256 id;
    address __seller;
    address __buyer;
    uint8 __type;
    uint8 __state;

    uint256 expiresAt;
    uint256 feePercentD1;

    address priceToken;

    // 0 = no buyout
    uint256 buyoutPrice;

    // 0 = no bids
    uint256 reservePrice;
    address lastBidder;
    uint256 lastBid;

    address token;
    uint256 tokenIdOrQuantity;
  }

  event ListingOpened(uint256 indexed id, address indexed sellerId, Listing listing, uint256 when);
  event ListingBid(uint256 indexed id, address indexed sellerId, address lastBidder, address nextBidder, uint256 bidAmount, uint256 bidSequence, uint256 when);
  event ListingClosed(uint256 indexed id, address indexed sellerId, address indexed buyerId, string reason, uint256 when);

  uint256 public immutable blockDeployed = block.number;

  ISettingsStore public settings;

  uint256 public nextBidSequence = 1000;
  uint256 public nextId = 1000;

  mapping(uint256 => Listing) public listings;
  mapping(address => mapping(uint256 => uint256)) public erc721ActiveListingLookup;

  mapping(address => bool) public allowedPriceTokens;
  mapping(address => bool) public allowedSellTokens;
  uint256 public auctionHouseFeePercentD1 = 100; // divided by 1000
  bool public allowNewAuctions = true;

  constructor(ISettingsStore _settings)
  {
    settings = _settings;
  }

  function setAllowNewAuctions(bool value) external {
    require(settings.isAdmin(msg.sender), "403");

    allowNewAuctions = value;
  }
  function setPriceTokens(address[] calldata allowedTokens, address[] calldata disallowedTokens) external {
    require(settings.isAdmin(msg.sender), "403");

    for(uint8 i = 0; i < allowedTokens.length; i++) {
      allowedPriceTokens[allowedTokens[i]] = true;
    }

    for(uint8 i = 0; i < disallowedTokens.length; i++) {
      allowedPriceTokens[disallowedTokens[i]] = false;
    }
  }
  function setSellTokens(address[] calldata allowedTokens, address[] calldata disallowedTokens) external {
    require(settings.isAdmin(msg.sender), "403");

    for(uint8 i = 0; i < allowedTokens.length; i++) {
      allowedSellTokens[allowedTokens[i]] = true;
    }

    for(uint8 i = 0; i < disallowedTokens.length; i++) {
      allowedSellTokens[disallowedTokens[i]] = false;
    }
  }
  function setAuctionHouseFeePercentD1(uint256 amountD1) external {
    require(settings.isAdmin(msg.sender), "403");

    // Amount must be less than 20%
    require(amountD1 < 200, "400");

    auctionHouseFeePercentD1 = amountD1;
  }

  function onERC721Received(address, address, uint256 id, bytes calldata) external override view returns (bytes4) {

    // msg.sender is the erc721 contract that invoked the method.
    uint256 listingId = erc721ActiveListingLookup[msg.sender][id];
    require(listingId > 0, "400|listingNotFound");

    require(listings[listingId].__state == LISTING_STATE_CREATED, "400|listingAlreadyOpen");

    return this.onERC721Received.selector;
  }

  function __paySeller(address to, IERC20 priceToken, uint256 price, uint256 feePercentD1) private {
    uint256 feeAmount = (price * feePercentD1) / 1000;
    uint256 sellerAmount = price - feeAmount;

    IAcceptTreasure treasury = IAcceptTreasure(settings.resolve("treasury.playeternity.io"));
    priceToken.approve(address(treasury), feeAmount);
    treasury.accept(priceToken, feeAmount);

    priceToken.safeTransfer(to, sellerAmount);
  }

  function __payBuyer(address to, uint8 listingType, address soldToken, uint256 tokenIdOrQuantity) private {
    if(listingType== LISTING_TYPE_ERC20) {
      IERC20(soldToken).safeTransfer(to, tokenIdOrQuantity);
    } else if(listingType == LISTING_TYPE_ERC721) {
      IERC721(soldToken).safeTransferFrom(address(this), to, tokenIdOrQuantity);
      erc721ActiveListingLookup[soldToken][tokenIdOrQuantity] = 0;
    }
  }

  function __refundBidder(address to, address priceToken, uint256 amount) private {
    IERC20(priceToken).safeTransfer(to, amount);
  }

  function createListing(uint8 listingType, address token, uint256 expiresInSeconds, uint256 tokenIdOrQuantity, address priceToken, uint256 buyoutPrice, uint256 reservePrice) external {

    require(allowNewAuctions, "400|newAuctionsDisabled");
    require(allowedPriceTokens[priceToken] == true, "400|priceTokenNotAllowed");
    require(allowedSellTokens[token] == true, "400|sellTokenNotAllowed");
    require(reservePrice > 0 || buyoutPrice > 0, "400|invalidPrice");
    require(expiresInSeconds == 0 || expiresInSeconds < 2 weeks, "400|expirationTooLong");
    if(reservePrice > 0) {
      require(expiresInSeconds > 0, "400|reserveRequiresExpiration");
      require(buyoutPrice == 0 || buyoutPrice > reservePrice, "400|reserveTooHigh");
    }

    uint256 listingId = nextId++;
    listings[listingId].id = listingId;
    listings[listingId].__seller = msg.sender;
    listings[listingId].__type = listingType;
    listings[listingId].__state = LISTING_STATE_CREATED;

    listings[listingId].expiresAt = expiresInSeconds > 0 ? block.timestamp + expiresInSeconds
      : 0;
    listings[listingId].feePercentD1 = auctionHouseFeePercentD1;

    listings[listingId].priceToken = priceToken;

    listings[listingId].buyoutPrice = buyoutPrice;

    listings[listingId].reservePrice = reservePrice;

    listings[listingId].token = token;
    listings[listingId].tokenIdOrQuantity = tokenIdOrQuantity;

    if(listingType == LISTING_TYPE_ERC20) {
      IERC20(token).safeTransferFrom(msg.sender, address(this), tokenIdOrQuantity);
    } else if(listingType == LISTING_TYPE_ERC721) {
      erc721ActiveListingLookup[token][tokenIdOrQuantity] = listingId;
      IERC721(token).safeTransferFrom(msg.sender, address(this), tokenIdOrQuantity);
    } else {
      require(false, "400|invalidListingType");
    }

    listings[listingId].__state = LISTING_STATE_OPEN;
    emit ListingOpened(listingId, msg.sender, listings[listingId], block.timestamp);
  }

  function cancelListing(uint256 id) external {

    require(listings[id].__state == LISTING_STATE_OPEN, "400|notOpen");
    require(listings[id].__seller == msg.sender, "403");
    require(listings[id].lastBid == 0, "400|reserveMet");

    listings[id].__state = LISTING_STATE_CLOSED;

    __payBuyer(listings[id].__seller, listings[id].__type, listings[id].token, listings[id].tokenIdOrQuantity);

    emit ListingClosed(id, listings[id].__seller, address(0), "Cancelled", block.timestamp);
  }

  function buyNow(uint256 id) external {

    require(listings[id].__state == LISTING_STATE_OPEN, "400|notOpen");
    require(listings[id].buyoutPrice > 0, "400|notForBuyout");
    require(listings[id].expiresAt == 0 || listings[id].expiresAt >= block.timestamp, "400|expired");

    if(listings[id].lastBid > 0) {
      __refundBidder(listings[id].lastBidder, listings[id].priceToken, listings[id].lastBid);
      listings[id].lastBid = 0;
      listings[id].lastBidder = address(0);
    }

    IERC20 priceToken = IERC20(listings[id].priceToken);
    uint256 price = listings[id].buyoutPrice;

    priceToken.safeTransferFrom(msg.sender, address(this), price);

    listings[id].__state = LISTING_STATE_CLOSED;
    listings[id].__buyer = msg.sender;

    __paySeller(listings[id].__seller, priceToken, listings[id].buyoutPrice, listings[id].feePercentD1);
    __payBuyer(msg.sender, listings[id].__type, listings[id].token, listings[id].tokenIdOrQuantity);

    emit ListingClosed(id, listings[id].__seller, listings[id].__buyer, "Buyout", block.timestamp);
  }

  function placeBid(uint256 id, uint256 nextBid) external {

    require(listings[id].__state == LISTING_STATE_OPEN, "400|notOpen");
    require(listings[id].reservePrice > 0, "400|notForBid");
    require(listings[id].expiresAt >= block.timestamp, "400|expired");

    // ensure next bid is sufficient
    require(nextBid >= listings[id].reservePrice, "400|reserveNotMet");
    require(nextBid >= listings[id].lastBid + 1e17, "400|bidTooLow");

    address lastBidder = listings[id].lastBidder;
    if(listings[id].lastBid > 0) {
      __refundBidder(listings[id].lastBidder, listings[id].priceToken, listings[id].lastBid);
      listings[id].lastBid = 0;
      listings[id].lastBidder = address(0);
    }

    IERC20(listings[id].priceToken).safeTransferFrom(msg.sender, address(this), nextBid);

    listings[id].lastBid = nextBid;
    listings[id].lastBidder = msg.sender;

    emit ListingBid(id, listings[id].__seller, lastBidder, msg.sender, nextBid, nextBidSequence++, block.timestamp);
  }

  function close(uint256 id) external {
    require(msg.sender == listings[id].__seller || msg.sender == listings[id].lastBidder, "403");
    require(listings[id].__state == LISTING_STATE_OPEN, "400|notOpen");
    require(listings[id].expiresAt < block.timestamp, "400|notExpired");
    require(listings[id].reservePrice > 0, "400|reserveNotSet");

    listings[id].__state = LISTING_STATE_CLOSED;

    bool hasBid = listings[id].lastBid > 0;
    listings[id].__buyer = hasBid ? listings[id].lastBidder
      : address(0);
    address sendPropertyTo = hasBid ? listings[id].__buyer
      : listings[id].__seller;

    __payBuyer(sendPropertyTo, listings[id].__type, listings[id].token, listings[id].tokenIdOrQuantity);

    // send tokens when sale is completed
    if(hasBid) {
      __paySeller(listings[id].__seller, IERC20(listings[id].priceToken), listings[id].lastBid, listings[id].feePercentD1);
    }

    emit ListingClosed(id, listings[id].__seller, listings[id].__buyer, hasBid ? "Sold" : "Failed", block.timestamp);
  }
}
