    "contracts/FixedYieldVault.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./mixins/ERC4626.sol";
import "./utils/DSMath.sol";
import "hardhat/console.sol";

/**
 * @title Remixed ERC4626 tokenized Vault implementation.
 */
contract FixedYieldVault is ERC4626, DSMath, Ownable {
    using SafeERC20 for IERC20;

    error InvalidAllowance();
    error InsufficientRewards();

    event Reward(address indexed distributor, address indexed receiver, uint256 assets);

    uint256 constant SECS_IN_DAY = 86400;

    // Vault deployment timestamp
    uint256 public createdAt;
    uint256 public closeAfterSecs;
    uint256 public unlockAfterSecs;

    // NSFW+ tokens deposited in vault
    uint256 public totalAssetsDeposited = 0;

    // Duration of lockup in days

    // Maximum capacity for this vault
    uint256 public vaultLimit;

    // Reference to NSFW+ contract
    IERC20 public immutable underlying;

    // eg. parseEther('0.1') // 10%
    uint256 public bonusPercentage;

    // Account holding rewards (MultiSig)
    address public distributor;

    // Vaults close 30 days from deployment
    modifier isVaultOpen() {
        require(depositClosesAt() > block.timestamp, "Vault: deposits closed");
        _;
    }

    constructor(
        IERC20 _underlying,
        uint256 _closeAfterSecs,
        uint256 _unlockAfterSecs,
        uint256 _vaultLimit,
        address _distributor,
        uint256 _bonus,
        string memory _name,
        string memory _symbol
    ) ERC4626(_underlying, _name, _symbol) {
        underlying = _underlying;
        closeAfterSecs = _closeAfterSecs;
        unlockAfterSecs = _unlockAfterSecs;
        vaultLimit = _vaultLimit;
        createdAt = block.timestamp;
        distributor = _distributor;
        bonusPercentage = _bonus;
    }

    /**
     * @notice Total assets sent to vault
     */
    function totalDeposits() public view returns (uint256) {
        return totalAssetsDeposited;
    }

    /**
     * @notice When the vault closes for deposits
     */
    function depositClosesAt() public view returns (uint256) {
        return createdAt + closeAfterSecs;
    }

    /**
     * @notice When the vault is open for withdrawals
     */
    function withdrawalOpensAt() public view returns (uint256) {
        return createdAt + unlockAfterSecs;
    }

    /**
     * @notice Total assets sent to vault
     */
    function maxCapacity() public view returns (uint256) {
        return vaultLimit;
    }

    /**
     * @notice Total assets remaining in vault
     */
    function totalAssets() public view override returns (uint256) {
        return underlying.balanceOf(address(this));
    }

    function deposit(uint256 assets, address receiver) public override isVaultOpen returns (uint256 shares) {
        // Check for rounding error since we round down in previewDeposit.
        require((shares = previewDeposit(assets)) != 0, "ZERO_SHARES");
        require(totalAssets() + assets < vaultLimit, "Vault: Exceeds capacity");

        // Need to transfer before minting or ERC777s could reenter.
        underlying.transferFrom(msg.sender, address(this), assets);

        _mint(receiver, shares);

        emit Deposit(msg.sender, receiver, assets, shares);

        afterDeposit(assets, shares);
    }

    function mint(uint256 shares, address receiver) public override isVaultOpen returns (uint256 assets) {
        // No need to check for rounding error, previewMint rounds up.
        assets = previewMint(shares);

        require(totalAssets() + assets < vaultLimit, "Vault: Exceeds capacity");

        // Need to transfer before minting or ERC777s could reenter.
        underlying.transferFrom(msg.sender, address(this), assets);

        _mint(receiver, shares);

        emit Deposit(msg.sender, receiver, assets, shares);

        afterDeposit(assets, shares);
    }

    function withdraw(
        uint256 assets,
        address receiver,
        address owner
    ) public override returns (uint256 shares) {
        shares = previewWithdraw(assets); // No need to check for rounding error, previewWithdraw rounds up.

        if (msg.sender != owner) {
            uint256 allowed = allowance[owner][msg.sender]; // Saves gas for limited approvals.
            if (allowed != type(uint256).max) allowance[owner][msg.sender] = allowed - shares;
        }

        beforeWithdraw(assets, shares);

        _burn(owner, shares);

        uint256 rewards = calculateRewards(assets);

        emit Withdraw(msg.sender, receiver, owner, assets, shares);
        emit Reward(distributor, receiver, rewards);

        underlying.transfer(receiver, assets);
        underlying.transferFrom(distributor, receiver, rewards);
    }

    function redeem(
        uint256 shares,
        address receiver,
        address owner
    ) public override returns (uint256 assets) {
        if (msg.sender != owner) {
            uint256 allowed = allowance[owner][msg.sender]; // Saves gas for limited approvals.
            if (allowed != type(uint256).max) allowance[owner][msg.sender] = allowed - shares;
        }

        // Check for rounding error since we round down in previewRedeem.
        require((assets = previewRedeem(shares)) != 0, "ZERO_ASSETS");

        beforeWithdraw(assets, shares);

        _burn(owner, shares);

        uint256 rewards = calculateRewards(assets);

        emit Withdraw(msg.sender, receiver, owner, assets, shares);
        emit Reward(distributor, receiver, rewards);

        underlying.transfer(receiver, assets);
        underlying.transferFrom(distributor, receiver, rewards);
    }

    // returns interest accrued as wad
    function calculateRewards(uint256 assets) public view returns (uint256) {
        uint256 _withdrawalOpensAt = withdrawalOpensAt();
        uint256 _rate = yearlyRateToRay(bonusPercentage);
        uint256 _age = (block.timestamp > _withdrawalOpensAt)
            ? _withdrawalOpensAt - createdAt
            : block.timestamp - createdAt;

        return sub(rmul(assets, rpow(_rate, _age)), assets);
    }

    function votingPower(uint256 assets) public view returns (uint256) {
        return assets + calculateRewards(assets);
    }

    /**
     * @dev Withdrawals only enabled after {n} days
     */
    function beforeWithdraw(uint256 assets, uint256) internal view override {
        require(withdrawalOpensAt() < block.timestamp, "Vault: Withdrawal disabled");
        uint256 rewards = calculateRewards(assets);
        if (underlying.allowance(distributor, address(this)) < rewards) revert InvalidAllowance();
        if (underlying.balanceOf(distributor) < rewards) revert InsufficientRewards();
    }

    /**
     * @dev Track total assets that were sent to this vault
     */
    function afterDeposit(uint256 _assets, uint256) internal override {
        totalAssetsDeposited = totalAssetsDeposited + _assets;
    }

    // Fixed point scale factors
    // wei -> the base unit
    // wad -> wei * 10 ** 18. 1 ether = 1 wad, so 0.5 ether can be used
    //      to represent a decimal wad of 0.5
    // ray -> wei * 10 ** 27
    // Go from wad (10**18) to ray (10**27)
    function wadToRay(uint256 _wad) internal pure returns (uint256) {
        return mul(_wad, 10**9);
    }

    // Go from wei to ray (10**27)
    function weiToRay(uint256 _wei) internal pure returns (uint256) {
        return mul(_wei, 10**27);
    }

    /**
     * @dev Takes in the desired nominal interest rate per year, compounded
     *   every second (this is approximately equal to nominal interest rate
     *   per year compounded continuously). Returns the ray value expected
     *   by the accrueInterest function
     * @param _rateWad A wad of the desired nominal interest rate per year,
     *   compounded continuously. Converting from ether to wei will effectively
     *   convert from a decimal value to a wad. So 5% rate = 0.05
     *   should be input as yearlyRateToRay( 0.05 ether )
     * @return 1 * 10 ** 27 + Effective Interest Rate Per Second * 10 ** 27
     */
    function yearlyRateToRay(uint256 _rateWad) public pure returns (uint256) {
        return add(wadToRay(1 ether), rdiv(wadToRay(_rateWad), weiToRay(365 * 86400)));
    }
}
