    "source/tournaments/MetaSoccerTournamentsV2.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";

contract MetaSoccerTournamentsV2 is AccessControlEnumerable, Pausable, EIP712
{
    using SafeERC20 for IERC20;

    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
	bytes32 public constant SIGN_REGISTRATION_ROLE = keccak256("SIGN_REGISTRATION_ROLE");
	bytes32 public constant SIGN_REWARDS_ROLE = keccak256("SIGN_REWARDS_ROLE");

    Tournament[] public tournaments;
    mapping(bytes32 => uint256) public tournamentsByTournamentId;

	Registration[] public registrations;
    mapping(bytes32 => uint256[]) public registrationsByTournamentId;
    mapping(bytes32 => uint256[]) public registrationsByTeamId;
    mapping(bytes32 => mapping(bytes32 => uint256)) public registrationsByTeamIdAndTournamentId;

    Sponsorship[] public sponsorships;
    mapping(bytes32 => uint256[]) public sponsorshipsByTournamentId;
    mapping(address => uint256[]) public sponsorshipsBySender;

    address public beneficiary;
    uint8 public beneficiaryFeePct;
    uint8 public creatorFeePct;

    event TeamRegistered(bytes32 indexed teamId, Tournament tournament, Registration registration);
	event TournamentSponsored(bytes32 indexed tournamentId, address indexed sender, uint256 amount, Tournament tournament);
	event TournamentRewardsClaimed(bytes32 indexed tournamentId, uint256 totalPrizeAmount, Tournament tournament);
	event TournamentRefunded(bytes32 indexed tournamentId, Tournament tournament);

    struct Tournament {
        bytes32 tournamentId;
        address paymentToken;
        address creator;
		uint256 entryFee;
        uint256 registrationCloseTimestamp;
		uint8 numberOfTeams;
        bool refunded;
        bool rewardsClaimed;
    }

    struct Registration {
		bytes32 tournamentId;
        bytes32 teamId;
        string registeredPlayerIds;
        address owner;
        address manager;
        address sender;
        uint8 rewardsSplitPct;
    }

    struct Sponsorship {
		bytes32 tournamentId;
        uint256 amount;
        address sender;
    }

    constructor(address _beneficiary, uint8 _beneficiaryFeePct, uint8 _creatorFeePct) EIP712("MetaSoccer", "1") {
        beneficiary = _beneficiary;
        beneficiaryFeePct = _beneficiaryFeePct;
        creatorFeePct = _creatorFeePct;
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

        // Push dummy tournament and registration to avoid handling cases for index 0
        tournaments.push(Tournament({
            tournamentId: "",
            paymentToken: address(0),
            creator: address(0),
            entryFee: 0,
            registrationCloseTimestamp: 0,
            numberOfTeams: 0,
            refunded: false,
            rewardsClaimed: false
        }));

        registrations.push(Registration({
            tournamentId: "",
            teamId: "",
            registeredPlayerIds: "",
            owner: address(0),
            manager: address(0),
            sender: address(0),
            rewardsSplitPct: 0
        }));
    }

    // Registration logic
    function register(
		bytes32 _tournamentId,
		uint256 _entryFee,
        uint256 _registrationCloseTimestamp,
        bytes32 _teamId,
		uint8 _numberOfTeams,
        uint8 _rewardsSplitPct,
        address[] calldata _addresses, // tournament creator, fee token, team owner, team manager
		string calldata _registeredPlayerIds,
		bytes calldata _signature
	) external whenNotPaused {
        require(_addresses.length == 4, "Addresses should include exactly tournament creator, fee token, team owner, team manager");
        require(msg.sender == _addresses[2] || msg.sender == _addresses[3], "Not owner nor manager");
        require(registrationsByTournamentId[_tournamentId].length < _numberOfTeams, "Tournament is full");
        require(registrationsByTeamIdAndTournamentId[_teamId][_tournamentId] == 0, "Team already registered");
        require(block.timestamp < _registrationCloseTimestamp, "Registration Closed");
        _verifyRegistrationRequestSignature(
            _tournamentId,
            _entryFee,
            _registrationCloseTimestamp,
            _teamId,
            _numberOfTeams,
            _rewardsSplitPct,
            _addresses, // tournament creator, fee token, team owner, team manager
		    _registeredPlayerIds,
		    _signature
        );
        Tournament memory tournament;
        if (tournamentsByTournamentId[_tournamentId] == 0) {
            tournament = _createTournament(_tournamentId, _entryFee, _registrationCloseTimestamp, _numberOfTeams, _addresses[0], _addresses[1]);
        } else {
            tournament = tournaments[tournamentsByTournamentId[_tournamentId]];
            _validateTournamentParams(tournament, _entryFee, _registrationCloseTimestamp, _numberOfTeams, _addresses[0], _addresses[1]);
        }
        IERC20(tournament.paymentToken).safeTransferFrom(msg.sender, address(this), tournament.entryFee);
        Registration memory registration = _createRegistration(
            _tournamentId,
            _teamId,
            _addresses[2],
            _addresses[3],
            _rewardsSplitPct,
            _registeredPlayerIds
        );

        emit TeamRegistered(_teamId, tournament, registration);
    }

    function sponsorTournament(bytes32 _tournamentId, uint256 _amount) external whenNotPaused {
        Tournament memory tournament = tournaments[tournamentsByTournamentId[_tournamentId]];
        require(tournament.refunded == false, "Already Refunded");
        require(tournament.rewardsClaimed == false, "Already claimed");
        IERC20(tournament.paymentToken).safeTransferFrom(msg.sender, address(this), _amount);
        Sponsorship memory sponsorship = _createSponsorship(_tournamentId, _amount);

        emit TournamentSponsored(_tournamentId, msg.sender, _amount, tournament);
    }


    // View Methods

    function getTournament(bytes32 _tournamentId) external view returns (Tournament memory) {       
        return tournaments[tournamentsByTournamentId[_tournamentId]];
    }

    function getRegisteredTeams(bytes32 _tournamentId) external view returns (Registration[] memory) {
        uint256 registeredTeamsLength = registrationsByTournamentId[_tournamentId].length;

        Registration[] memory ret = new Registration[](registeredTeamsLength);
        for (uint256 i = 0; i < registeredTeamsLength; i++) {
            ret[i] = registrations[registrationsByTournamentId[_tournamentId][i]];
        }

        return ret;
    }

    function isTournamentFull(bytes32 _tournamentId) external view returns (bool) {
        Tournament memory tournament = tournaments[tournamentsByTournamentId[_tournamentId]];
        return registrationsByTournamentId[_tournamentId].length == tournament.numberOfTeams;
    }

    function getTournamentPrizePool(bytes32 _tournamentId) public view returns (uint256) {
        Tournament memory tournament = tournaments[tournamentsByTournamentId[_tournamentId]];
        uint256 sponsorshipsAmount = 0;
        for (uint256 i = 0; i < sponsorshipsByTournamentId[_tournamentId].length; i++) {
           sponsorshipsAmount += sponsorships[sponsorshipsByTournamentId[_tournamentId][i]].amount;
        }

        return tournament.entryFee * tournament.numberOfTeams + sponsorshipsAmount;
    }

    function getTournamentsForTeam(bytes32 _teamId) external view returns (bytes32[] memory) {   
        bytes32[] memory tournaments = new bytes32[](registrationsByTeamId[_teamId].length);

        for (uint256 i = 0; i < registrationsByTeamId[_teamId].length; i++) {
            tournaments[i] = registrations[registrationsByTeamId[_teamId][i]].tournamentId;
        }

        return tournaments;
    }


    // Claim functions

    function claimRewards(
        bytes32 _tournamentId,
        bytes32[] calldata _teamIds,
        uint8[] calldata _prizePcts,
        bytes calldata _signature
    ) external whenNotPaused {
        require(_prizePcts.length == _teamIds.length, "Teams and prizes size must match");
        _verifyClaimPrizesRequestSignature(
            _tournamentId,
            _teamIds,
            _prizePcts,
		    _signature
        );

        Tournament memory tournament = tournaments[tournamentsByTournamentId[_tournamentId]];

        require(tournament.refunded == false, "Already Refunded");
        require(tournament.rewardsClaimed == false, "Already claimed");
        require(registrationsByTournamentId[_tournamentId].length == tournament.numberOfTeams, "Tournament not full");

        uint256 totalPrizePool = getTournamentPrizePool(_tournamentId);
        uint256 beneficiaryFee = totalPrizePool * beneficiaryFeePct / 100;
        uint256 creatorFee = totalPrizePool * creatorFeePct / 100;
        IERC20(tournament.paymentToken).safeTransfer(beneficiary, beneficiaryFee);
        IERC20(tournament.paymentToken).safeTransfer(tournament.creator, creatorFee);

        uint256 prizePoolAfterFees = totalPrizePool - beneficiaryFee - creatorFee;
        for (uint256 i = 0; i < _teamIds.length; i++) {
            Registration memory registration = registrations[registrationsByTeamIdAndTournamentId[_teamIds[i]][tournament.tournamentId]];
            uint256 teamReward = prizePoolAfterFees * _prizePcts[i] / 100;
            if (registration.rewardsSplitPct < 100) {
                uint256 managerReward = teamReward * registration.rewardsSplitPct / 100;
                IERC20(tournament.paymentToken).safeTransfer(registration.manager, managerReward);
                IERC20(tournament.paymentToken).safeTransfer(registration.owner, teamReward - managerReward);
            } else {
                IERC20(tournament.paymentToken).safeTransfer(registration.manager, teamReward);
            }
        }

        tournament.rewardsClaimed = true;
        tournaments[tournamentsByTournamentId[_tournamentId]] = tournament;

        emit TournamentRewardsClaimed(_tournamentId, totalPrizePool, tournament);
    }

    // Claim entry fee if not enough members
    function claimEntryFees(bytes32 _tournamentId) external {
        Tournament memory tournament = tournaments[tournamentsByTournamentId[_tournamentId]];

        // Only withdraw entry fee if not enough teams registered and registration is closed
        require(tournament.refunded == false, "Already refunded");
        require(tournament.rewardsClaimed == false, "Already claimed");
        require(block.timestamp > tournament.registrationCloseTimestamp, "Registration not closed yet");
        uint256 registeredTeamsLength = registrationsByTournamentId[_tournamentId].length;
        require(registeredTeamsLength < tournament.numberOfTeams, "Tournament is full");

        // Return entry fees
        for (uint256 i = 0; i < registeredTeamsLength; i++) {
            Registration memory registration = registrations[registrationsByTournamentId[_tournamentId][i]];
            IERC20(tournament.paymentToken).safeTransfer(registration.sender, tournament.entryFee);
        }

        // Return sponsorships
        for (uint256 i = 0; i < sponsorshipsByTournamentId[_tournamentId].length; i++) {
           Sponsorship memory sponsorship = sponsorships[sponsorshipsByTournamentId[_tournamentId][i]];
           IERC20(tournament.paymentToken).safeTransfer(sponsorship.sender, sponsorship.amount);
        }

        tournament.refunded = true;
        tournaments[tournamentsByTournamentId[_tournamentId]] = tournament;

        emit TournamentRefunded(_tournamentId, tournament);
    }

    // Admin logic
    function setBeneficiary(address _beneficiary) external onlyRole(DEFAULT_ADMIN_ROLE) {
        beneficiary = _beneficiary;
    }

    function setBeneficiaryFee(uint8 _beneficiaryFeePct) external onlyRole(DEFAULT_ADMIN_ROLE) {
        beneficiaryFeePct = _beneficiaryFeePct;
    }

    function setCreatorFee(uint8 _creatorFeePct) external onlyRole(DEFAULT_ADMIN_ROLE) {
        creatorFeePct = _creatorFeePct;
    }

    function setPause(bool pause) external onlyRole(PAUSER_ROLE) {
        if (pause) {
            _pause();
        } else {
            _unpause();
        }
    }

    ///@dev Withdraw function to avoid locking tokens in the contract
    function withdrawERC20(IERC20 _token, uint256 _amount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _token.safeTransfer(msg.sender, _amount);
    }

    ///@dev Emergency method to withdraw NFT in case someone sends..
    function withdrawNFT(IERC721 _token, uint256 _tokenId) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _token.safeTransferFrom(address(this), msg.sender, _tokenId);
    }

    // Internal functions
    function _createTournament(
        bytes32 _tournamentId,
		uint256 _entryFee,
        uint256 _registrationCloseTimestamp,
		uint8 _numberOfTeams,
        address _owner,
        address _paymentToken
    ) internal returns (Tournament memory) {
        Tournament memory tournament = Tournament({
            tournamentId: _tournamentId,
            paymentToken: _paymentToken,
            creator: _owner,
            entryFee: _entryFee,
            registrationCloseTimestamp: _registrationCloseTimestamp,
            numberOfTeams: _numberOfTeams,
            refunded: false,
            rewardsClaimed: false
        });
        tournamentsByTournamentId[_tournamentId] = tournaments.length;
        tournaments.push(tournament);

        return tournament;
    }

    function _createRegistration(
        bytes32 _tournamentId,
        bytes32 _teamId,
        address _owner,
        address _manager,
        uint8 _rewardsSplitPct,
		string calldata _registeredPlayerIds
    ) internal returns (Registration memory) {
        require(_rewardsSplitPct <= 100, "Invalid rewards Split");
        Registration memory registration = Registration({
            tournamentId: _tournamentId,
            teamId: _teamId,
            registeredPlayerIds: _registeredPlayerIds,
            owner: _owner,
            manager: _manager,
            sender: msg.sender,
            rewardsSplitPct: _rewardsSplitPct
        });
        uint256 registrationIndex = registrations.length;
        registrationsByTournamentId[_tournamentId].push(registrationIndex);
        registrationsByTeamId[_teamId].push(registrationIndex);
        registrationsByTeamIdAndTournamentId[_teamId][_tournamentId] = registrationIndex;
        registrations.push(registration);

        return registration;
    }

    function _createSponsorship(
        bytes32 _tournamentId,
        uint256 _amount
    ) internal returns (Sponsorship memory) {
        Sponsorship memory sponsorship = Sponsorship({
            tournamentId: _tournamentId,
            amount: _amount,
            sender: msg.sender
        });
        uint256 sponsorshipIndex = sponsorships.length;
        sponsorshipsByTournamentId[_tournamentId].push(sponsorshipIndex);
        sponsorshipsBySender[msg.sender].push(sponsorshipIndex);
        sponsorships.push(sponsorship);

        return sponsorship;
    }

    function _verifyRegistrationRequestSignature(
        bytes32 _tournamentId,
		uint256 _entryFee,
        uint256 _registrationCloseTimestamp,
        bytes32 _teamId,
		uint8 _numberOfTeams,
        uint8 _rewardsSplitPct,
        address[] calldata _addresses, // fee token, creator, owner, manager
		string calldata _registeredPlayerIds,
		bytes calldata _signature
    ) internal {
        bytes32 digest = _hashTypedDataV4(keccak256(abi.encode(
            keccak256("RegistrationRequest(bytes32 tournamentId,uint256 entryFee,uint256 registrationCloseTimestamp,bytes32 teamId,uint8 numberOfTeams,uint8 rewardsSplitPct,address[] addresses,string registeredPlayerIds)"),
            _tournamentId,
            _entryFee,
            _registrationCloseTimestamp,
            _teamId,
            _numberOfTeams,
            _rewardsSplitPct,
            keccak256(abi.encodePacked(_addresses)),
            keccak256(bytes(_registeredPlayerIds))
        )));
        address signer = ECDSA.recover(digest, _signature);
        require(hasRole(SIGN_REGISTRATION_ROLE, signer), "Invalid registration signer");
    }

    function _verifyClaimPrizesRequestSignature(
        bytes32 _tournamentId,
        bytes32[] calldata _teamIds,
        uint8[] calldata _prizePcts,
        bytes calldata _signature
    ) internal {
        bytes32 digest = _hashTypedDataV4(keccak256(abi.encode(
            keccak256("ClaimPrizesRequest(bytes32 tournamentId,bytes32[] teamIds,uint8[] prizePcts)"),
            _tournamentId,
            keccak256(abi.encodePacked(_teamIds)),
            keccak256(abi.encodePacked(_prizePcts))
        )));
        address signer = ECDSA.recover(digest, _signature);
        require(hasRole(SIGN_REWARDS_ROLE, signer), "Invalid rewards signer");
    }

    function _validateTournamentParams(
        Tournament memory tournament,
        uint256 _entryFee,
        uint256 _registrationCloseTimestamp,
        uint8 _numberOfTeams,
        address _creator,
        address _paymentToken
    ) internal {
        require(
            tournament.entryFee == _entryFee &&
            tournament.registrationCloseTimestamp == _registrationCloseTimestamp &&
            tournament.numberOfTeams == _numberOfTeams &&
            tournament.creator == _creator &&
            tournament.paymentToken == _paymentToken
            , "Invalid Tournament Params"
        );
    }
}"
    }
  }
