pragma solidity ^0.8.0;



/**
 * @title SwapToken
 * @dev A swap contract which is utilised to provide swap functioanlities between New and legacy TVK Tokens.
 */
contract TVKSwap is Ownable, ReentrancyGuard {

    address public constant LEGACY_TVK_TOKEN = 0x5667dcC0ab74D1b1355C3b2061893399331B57e2;
    address public constant VANRY_TOKEN = 0x8DE5B80a0C1B02Fe4976851D030B36122dbb8624;

    bool public swapStatus;

    event TokensSwapped(
        address indexed _beneficiary, 
        uint256 _amount
    );
    event TokenSwapPaused(
        bool _status,
        string _context
    );
     event TokenSwapUnpaused(
        bool _status, 
        string _context
    );  
    event TokensWithdrawn(
        address indexed _beneficiary,
        uint256 _amount,
        address indexed _tokenAddress
    );

    constructor() Ownable() {
        
    }

    /**
     * @dev Swap legacy TVK tokens with new TVK tokens.
     * @param _amount The amount of tokens to swap.
     */

    function swapToken(
        uint256 _amount
    ) public nonReentrant {

        require(
            _amount != 0, 
            "Swap Contract: Invalid amount!"
        );
        require(
            swapStatus, 
            "Swap Contract: Swap is not enabled!"
        );

        IERC20(LEGACY_TVK_TOKEN).transferFrom(msg.sender, address(this), _amount);

        IERC20(VANRY_TOKEN).transfer(msg.sender, _amount);

        emit TokensSwapped(msg.sender, _amount);
    }

    /**
     * @dev Multisig owner based functionality which is used to withdraw remaining tokens of new TVK and legacy TVK.
     * it can only withdraw the legacy TVK tokens which will be deposited by the users for swap and the remaining new TVK tokens from swap contract.
     *
     * @param _beneficiary The address where token will be withdrawn.
     * @param _amount The amount of tokens to be withdrawn.
     * @param _tokenAddress The address must belogs to new or leagacy TVK tokens contract.
     */

    function withdrawToken(
        address _beneficiary,
        uint256 _amount,
        address _tokenAddress
    ) public onlyOwner nonReentrant {

        require(
            _amount != 0, 
            "Swap Contract: Invalid amount!"
        );
        require(
            _beneficiary != address(0), 
            "Swap Contract: Invalid address!"
        );

        IERC20(_tokenAddress).transfer(_beneficiary, _amount);

        emit TokensWithdrawn(_beneficiary, _amount, _tokenAddress);
    }

    /**
     * @dev Owner based functionality which will disable swap feature.
     * @param _context The purpose of pausing swap.
     */

    function pauseSwap(string memory _context) public onlyOwner {
        bytes memory contextInBytes = bytes(_context);
        require(swapStatus == true, "Swap Contract: Swap already paused.");
        require(contextInBytes.length != 0, "Swap Contract: Context not be empty.");
        
        swapStatus = false;
        emit TokenSwapPaused(swapStatus, _context);
    }

    /**
     * @dev Owner based functionality which will enable swap feature. This feature can only be enabled when swap contract have some new token liquidity to be swapped.
     * @param _context The purpose of unpausing swap.
     */

    function unpauseSwap(string memory _context) public onlyOwner {
        bytes memory contextInBytes = bytes(_context);
        require(swapStatus == false, "Swap Contract: Swap already unpaused.");
        require(contextInBytes.length != 0, "Swap Contract: Context not be empty.");
        require(IERC20(VANRY_TOKEN).balanceOf(address(this)) != 0, "Swap Contract: Not enough liquidity for token swap");
        
        swapStatus = true;
        emit TokenSwapUnpaused(swapStatus, _context);
    }

    /**
     * @dev Get balance of legacy TVK tokens in swap contract.
     * @return The balance of legacy TVK tokens in swap contract.
     */


    function balanceOfLegacyToken() public view returns (uint256) {

        return IERC20(LEGACY_TVK_TOKEN).balanceOf(address(this));

    }

    /**
     * @dev Get balance of new TVK tokens in swap contract.
     * @return The balance of new TVK tokens in swap contract.
     */

    function balanceOfNewToken() public view returns (uint256) {

        return IERC20(VANRY_TOKEN).balanceOf(address(this));

    }
}
